// src/components/Header.js
import React from 'react';
import styled from 'styled-components';
import { Flex, Heading, Link, Spacer, UnorderedList, ListItem } from '@chakra-ui/react';
import { css } from '@emotion/react';

const StyledHeader = styled(Flex)`
  align-items: center;
  justify-content: space-between;
  background-color: teal;
  color: white;
  padding: 4;
  margin-bottom: 8;
`;

const EmotionHeader = styled(Flex)`
  ${({ theme }) => css`
    align-items: center;
    justify-content: space-between;
    background-color: ${theme.colors.teal[500]};
    color: ${theme.colors.white};
    padding: 4;
    margin-bottom: 8;
  `}
`;

const Header = () => {
  return (
    <>
      <StyledHeader as="header">
        <Heading as="h1" size="md">
          Seu Nome
        </Heading>
        <Spacer />
        <UnorderedList listStyleType="none" display="flex" justifyContent="space-around">
          <ListItem cursor="pointer" fontWeight="bold">
            <Link href="#about">Sobre Mim</Link>
          </ListItem>
          <ListItem cursor="pointer" fontWeight="bold">
            <Link href="#projects">Projetos</Link>
          </ListItem>
          <ListItem cursor="pointer" fontWeight="bold">
            <Link href="#contact">Contato</Link>
          </ListItem>
        </UnorderedList>
      </StyledHeader>

      <EmotionHeader as="header">
        <Heading as="h1" size="md">
          Seu Nome
        </Heading>
        <Spacer />
        <UnorderedList listStyleType="none" display="flex" justifyContent="space-around">
          <ListItem cursor="pointer" fontWeight="bold">
            <Link href="#about">Sobre Mim</Link>
          </ListItem>
          <ListItem cursor="pointer" fontWeight="bold">
            <Link href="#projects">Projetos</Link>
          </ListItem>
          <ListItem cursor="pointer" fontWeight="bold">
            <Link href="#contact">Contato</Link>
          </ListItem>
        </UnorderedList>
      </EmotionHeader>
    </>
  );
};

export default Header;
