import React from 'react';
import styled from 'styled-components';
import { Box, Text, Image } from '@chakra-ui/react';
import { css } from '@emotion/react';

const palette = {
  primary: '#0F0F0F',
  secondary: '#232D3F',
  accent: '#005B41',
  highlight: '#008170',
};

const StyledCertificateViewer = styled(Box)`
  background-color: ${palette.primary};
  color: ${palette.highlight};
  padding: 4;
  margin-bottom: 8;
  text-align: center;
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  justify-content: center;
`;

const CertificateViewer = ({ imageUrl }) => (
  <StyledCertificateViewer>
    <Text>Meus Certificados</Text>
    <Box>
      <Image src='/img/DesingnResponsivo.png' alt="Certificado" borderRadius="md" mt="4" maxW="400px" mx="auto" />
      <Image src='/img/DesingnResponsivo.png' alt="Certificado" borderRadius="md" mt="4" maxW="400px" mx="auto" />
      <Image src='/img/DesingnResponsivo.png' alt="Certificado" borderRadius="md" mt="4" maxW="400px" mx="auto" />
      <Image src='/img/DesingnResponsivo.png' alt="Certificado" borderRadius="md" mt="4" maxW="400px" mx="auto" />
    </Box>
  </StyledCertificateViewer>
);

export default CertificateViewer;
