import React, { useState } from 'react';
import styled from 'styled-components';
import { Box, Heading, FormControl, FormLabel, Input, Textarea, Button } from '@chakra-ui/react';
import axios from 'axios';

const palette = {
  primary: '#0F0F0F',
  accent: '#005B41',
  highlight: '#008170',
};

const StyledContact = styled(Box)`
  background-color: ${palette.primary};
  color: ${palette.highlight};
  padding: 4;
  margin-bottom: 8;
  text-align: center;
`;

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Dados do e-mail
    const { name, email, message } = formData;

    // Enviar e-mail
    try {
      const params = new URLSearchParams();
      params.append('name', name);
      params.append('email', email);
      params.append('message', message);

      const response = await axios.post('http://localhost:3001/send-email', params, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
      });

      alert(response.data.message);
    } catch (error) {
      console.error('Erro ao enviar e-mail:', error);
      alert('Erro ao enviar e-mail. Verifique o console para mais detalhes.');
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <StyledContact id="contact">
      <Heading as="h2" size="lg" mb="4">
        Contato
      </Heading>
      <form>
        <FormControl mb="4">
          <FormLabel htmlFor="name">Nome:</FormLabel>
          <Input type="text" id="name" placeholder="Seu Nome" onChange={handleChange} name="name" />
        </FormControl>
        <FormControl mb="4">
          <FormLabel htmlFor="email">E-mail:</FormLabel>
          <Input type="email" id="email" placeholder="Seu E-mail" onChange={handleChange} name="email" />
        </FormControl>
        <FormControl mb="4">
          <FormLabel htmlFor="message">Mensagem:</FormLabel>
          <Textarea id="message" placeholder="Sua Mensagem" onChange={handleChange} name="message" />
        </FormControl>
        <Button type="submit" bgColor={palette.accent} color={palette.primary} _hover={{ bgColor: palette.highlight }} onClick={handleSubmit}>
          Enviar
        </Button>
      </form>
    </StyledContact>
  );
};

export default Contact;
