import React from 'react';
import styled from 'styled-components';
import { Box, Heading, UnorderedList, ListItem, Text, HStack } from '@chakra-ui/react';
// import { FaHtml5, FaCss3, FaJs, FaReact, FaNodeJs, FaNpm, FaDatabase, FaGit, FaSwift, FaAngular, FaVuejs, FaTailwind, FaTypescript } from 'react-icons/fa';

const palette = {
  primary: '#0F0F0F',
  accent: '#005B41',
  highlight: '#008170',
};

const StyledTechSection = styled(Box)`
  background-color: ${palette.primary};
  color: ${palette.highlight};
  padding: 4;
  margin-bottom: 8;
  text-align: center;
`;

const TechList = styled(UnorderedList)`
  list-style: none;
  padding: 0;
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  gap: 4;
`;

const TechListItem = styled(ListItem)`
  display: flex;
  align-items: center;
  margin-bottom: 2;
  color: ${palette.accent};
`;

const TechIcon = styled.div`
  font-size: 2rem;
  margin-right: 1rem;
`;

const techData = [
    { name: 'HTML', icon: <FaHtml5 /> },
    { name: 'CSS', icon: <FaCss3 /> },
    { name: 'JavaScript', icon: <FaJs /> },
    { name: 'React', icon: <FaReact /> },
    { name: 'React Router', icon: <FaReact /> },
    { name: 'Tailwind CSS', icon: <FaTailwind /> },
    { name: 'Styled-Components', icon: <FaReact /> },
    { name: 'Vue.js', icon: <FaVuejs /> },
    { name: 'Node.js', icon: <FaNodeJs /> },
    { name: 'Express', icon: <FaNodeJs /> },
    { name: 'TypeScript', icon: <FaTypescript /> },
    { name: 'MongoDB', icon: <FaDatabase /> },
    { name: 'SQLite', icon: <FaDatabase /> },
    { name: 'Git', icon: <FaGit /> },
    { name: 'Swift', icon: <FaSwift /> },
    { name: 'Chakra UI', icon: <FaReact /> },
    { name: 'Angular', icon: <FaAngular /> },
  ];

const TechSection = () => {
  return (
    <StyledTechSection>
      <Heading as="h2" size="lg" mb="4" color={palette.accent}>
        Tecnologias que eu conhe√ßo
      </Heading>
      <TechList>
        {techData.map((tech, index) => (
          <TechListItem key={index}>
            <TechIcon>{tech.icon}</TechIcon>
            <Text>{tech.name}</Text>
          </TechListItem>
        ))}
      </TechList>
    </StyledTechSection>
  );
};

export default TechSection;
