// src/components/Header.js
import React from 'react';
import styled from 'styled-components';
import { Flex, Heading, Link as ChakraLink, Spacer, UnorderedList, ListItem } from '@chakra-ui/react';
import { css } from '@emotion/react';

const palette = {
  primary: '#0F0F0F',
  secondary: '#232D3F',
  accent: '#005B41',
  highlight: '#008170',
};

const StyledHeader = styled(Flex)`
  align-items: center;
  justify-content: space-between;
  background-color: ${palette.secondary};
  color: ${palette.highlight};
  padding: 4;
  margin-bottom: 8;
`;

const EmotionHeader = styled(Flex)`
  ${({ theme }) => css`
    align-items: center;
    justify-content: space-between;
    background-color: ${theme.colors[palette.secondary]};
    color: ${theme.colors[palette.highlight]};
    padding: 4;
    margin-bottom: 8;
  `}
`;

const StyledLink = styled(ChakraLink)`
  color: inherit;
  text-decoration: none;
  font-weight: bold;
  transition: color 0.3s ease;

  &:hover {
    color: ${palette.accent};
  }
`;

const Header = () => {
  return (
    <>
      <StyledHeader as="header">
        <Heading as="h1" size="md">
          Seu Nome
        </Heading>
        <Spacer />
        <UnorderedList listStyleType="none" display="flex" justifyContent="space-around">
          <ListItem>
            <StyledLink href="#about">Sobre Mim</StyledLink>
          </ListItem>
          <ListItem>
            <StyledLink href="#projects">Projetos</StyledLink>
          </ListItem>
          <ListItem>
            <StyledLink href="#contact">Contato</StyledLink>
          </ListItem>
        </UnorderedList>
      </StyledHeader>

      <EmotionHeader as="header">
        <Heading as="h1" size="md">
          Seu Nome
        </Heading>
        <Spacer />
        <UnorderedList listStyleType="none" display="flex" justifyContent="space-around">
          <ListItem>
            <StyledLink href="#about">Sobre Mim</StyledLink>
          </ListItem>
          <ListItem>
            <StyledLink href="#projects">Projetos</StyledLink>
          </ListItem>
          <ListItem>
            <StyledLink href="#contact">Contato</StyledLink>
          </ListItem>
        </UnorderedList>
      </EmotionHeader>
    </>
  );
};

export default Header;
