import React from 'react';
import styled from 'styled-components';
import { Box, Text, Image } from '@chakra-ui/react';
import { PDFViewer } from '@react-pdf/renderer';

const palette = {
  primary: '#0F0F0F',
  secondary: '#232D3F',
  accent: '#005B41',
  highlight: '#008170',
};

const certificates = [
  { src: '/img/DesingnResponsivo.png', alt: 'Certificado 1' },
  { src: '/img/JavaScriptAlgorithms.png', alt: 'Certificado 2' },
  { src: '/img/BibliotecasFront.png', alt: 'Certificado 3' },
  { src: '/img/VisualizacaoDados.png', alt: 'Certificado 4' },
];

const pdfCertificates = {
  '/pdf/Certificado_ICL.pdf': 'Certificado 1',
};

const StyledCertificateViewer = styled(Box)`
  background-color: ${palette.primary};
  color: ${palette.highlight};
  padding: 4;
  margin-bottom: 8;
  text-align: center;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
`;

const CertificateViewer = ({ imageUrl }) => (
  <StyledCertificateViewer>
    <Text>Meus Certificados</Text>
    {certificates.map((certificate, index) => (
      <Box key={index} mt="4" maxW="400px" mx="auto">
        <Image src={certificate.src} alt={certificate.alt} borderRadius="md" />
      </Box>
    ))}
    <Box>
      {Object.entries(pdfCertificates).map(([pdfUrl, alt]) => (
        <PDFViewer key={pdfUrl} src={pdfUrl} alt={alt} />
      ))}
    </Box>
  </StyledCertificateViewer>
);

export default CertificateViewer;
