// src/components/AboutMe.js
import React from 'react';
import styled from 'styled-components';
import { Box, Heading, Text, Image } from '@chakra-ui/react';
import { css } from '@emotion/react';

const palette = {
  primary: '#0F0F0F',
  secondary: '#232D3F',
  accent: '#005B41',
  highlight: '#008170',
};

const StyledAboutMe = styled(Box)`
  background-color: ${palette.primary};
  color: ${palette.highlight};
  padding: 4;
  margin-bottom: 8;
  text-align: center;
`;

const EmotionAboutMe = styled(Box)`
  ${({ theme }) => css`
    background-color: ${theme.colors[palette.primary]};
    color: ${theme.colors[palette.highlight]};
    padding: 4;
    margin-bottom: 8;
    text-align: center;
  `}
`;

const Frame = styled(Box)`
  position: relative;
  overflow: hidden;
  width: 150px;
  height: 150px;
  margin: 0 auto;
  border: 5px solid ${palette.accent};
  border-radius: 50%;
  transition: transform 0.3s ease-in-out;

  &:hover {
    opacity: 0.8;
  }
`;

const AboutMe = () => {
  return (
    <>
      <StyledAboutMe id="about">
        <Heading as="h2" size="lg" mb="4">
          Sobre Mim
        </Heading>
        <Frame>
          <Image src="/img/eu.JPG" alt="Sua Foto" borderRadius="50%" />
        </Frame>
        <Text mt="4">
          Coloque aqui sua descrição sobre você. Conte suas experiências, habilidades e o que te motiva no mundo da
          tecnologia.
        </Text>
      </StyledAboutMe>
    </>
  );
};

export default AboutMe;
