// src/components/Contact.js
import React, {useState} from 'react';
import styled from 'styled-components';
import { Axios } from 'axios';
import { Box, Heading, FormControl, FormLabel, Input, Textarea, Button } from '@chakra-ui/react';

const palette = {
  primary: '#0F0F0F',
  accent: '#005B41',
  highlight: '#008170',
};

const StyledContact = styled(Box)`
  background-color: ${palette.primary};
  color: ${palette.highlight};
  padding: 4;
  margin-bottom: 8;
  text-align: center;
`;

const Contact = () => {
  return (
    <StyledContact id="contact">
      <Heading as="h2" size="lg" mb="4">
        Contato
      </Heading>
      <form>
        <FormControl mb="4">
          <FormLabel htmlFor="name">Nome:</FormLabel>
          <Input type="text" id="name" placeholder="Seu Nome" />
        </FormControl>
        <FormControl mb="4">
          <FormLabel htmlFor="email">E-mail:</FormLabel>
          <Input type="email" id="email" placeholder="Seu E-mail" />
        </FormControl>
        <FormControl mb="4">
          <FormLabel htmlFor="message">Mensagem:</FormLabel>
          <Textarea id="message" placeholder="Sua Mensagem" />
        </FormControl>
        <Button type="submit" bgColor={palette.accent} color={palette.primary} _hover={{ bgColor: palette.highlight }}>
          Enviar
        </Button>
      </form>
    </StyledContact>
  );
};

export default Contact;
