import React, { useState, useEffect } from 'react';
import { VStack, Heading, Box, Text, Input, Textarea, Button, List, ListItem, Icon } from '@chakra-ui/react';
import { EmailIcon, ExternalLinkIcon, PhoneIcon } from '@chakra-ui/icons';
import styled from 'styled-components'
import axios from 'axios';

const palette = {
  primary: '#0F0F0F',
  accent: '#005B41',
  highlight: '#008170',
};

const StyledBlog = styled(Box)`
  background-color: ${palette.primary};
  color: ${palette.highlight};
  padding: 4;
  margin-bottom: 8;
  text-align: center;
`;

const CenteredListItem = styled(ListItem)`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-bottom: 2;
`;

const Blog = () => {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState({ title: '', content: '' });

  useEffect(() => {
    axios.get('http://localhost:5000/posts')
      .then(response => setPosts(response.data))
      .catch(error => console.error(error));
  }, []);

  const handleInputChange = (e) => {
    setNewPost({ ...newPost, [e.target.name]: e.target.value });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();

    axios.post('http://localhost:5000/posts', newPost)
      .then(response => {
        setPosts([...posts, response.data]);
        setNewPost({ title: '', content: '' });
      })
      .catch(error => console.error(error));
  };

  return (
    <StyledBlog>
      <Heading as="h2" size="xl" mb="4" color={palette.accent}>
        Últimas Postagens do Blog
      </Heading>
      <form onSubmit={handleFormSubmit}>
        <Input
          type="text"
          name="title"
          placeholder="Título"
          value={newPost.title}
          onChange={handleInputChange}
          mb="2"
        />
        <Textarea
          name="content"
          placeholder="Conteúdo"
          value={newPost.content}
          onChange={handleInputChange}
          mb="2"
        />
        <Button type="submit" colorScheme="teal">Criar Postagem</Button>
      </form>
      <VStack spacing={4} align="stretch" mt="4">
        {posts.map(post => (
          <Box key={post._id} p={4} borderWidth="1px" borderRadius="md">
            <Heading as="h3" size="lg" mb={2}>{post.title}</Heading>
            <Text>{post.content}</Text>
          </Box>
        ))}
      </VStack>
    </StyledBlog>
  );
};

export default Blog;
