// src/components/Footer.js
import React from 'react';
import styled from 'styled-components';
import { Box, Link } from '@chakra-ui/react';

const palette = {
  primary: '#0F0F0F',
  secondary: '#232D3F',
  accent: '#005B41',
  highlight: '#008170',
};

const StyledFooter = styled(Box)`
  background-color: ${palette.primary};
  color: ${palette.highlight};
  padding: 4;
  text-align: center;
  position: fixed;
  bottom: 0;
  width: 100%;
`;

const Footer = () => {
  return (
    <StyledFooter>
      <p>
        © {new Date().getFullYear()} Juliano Amaral. Todos os direitos reservados.
      </p>
      <p>
        Feito com ❤️ por <Link href="https://github.com/seu-usuario" target="_blank" rel="noopener noreferrer">Seu Nome</Link>
      </p>
    </StyledFooter>
  );
};

export default Footer;
