import React, { useState, useEffect } from 'react';
import { VStack, Heading, Box, Text } from '@chakra-ui/react';
import axios from 'axios';
import styled from 'styled-components';

const palette = {
    primary: '#0F0F0F',
    accent: '#005B41',
    highlight: '#008170',
};

const StyledBlog = styled(Box)`
  background-color: ${palette.primary};
  color: ${palette.highlight};
  padding: 4;
  margin-bottom: 8;
  text-align: center;
  height: 100%;
`;

const Blog = () => {
    const [posts, setPosts] = useState([]);
    const author = "Juliano Amaral";

    useEffect(() => {
        axios.get(`http://localhost:5000/posts?author=${author}`)
            .then(response => setPosts(response.data))
            .catch(error => console.error(error));
    }, [author]);

    return (
        <StyledBlog>
            <Heading as="h2" size="xl" mb="4" color={palette.accent}>
                Últimas Postagens do Blog
            </Heading>
            <VStack spacing={4} align="stretch" mt="4">
                {posts.map(post => (
                    <Box key={post._id} p={4} borderWidth="1px" borderRadius="md">
                        <Heading as="h3" size="lg" mb={2}>
                            {post.title}
                        </Heading>
                        <Text>{post.content}</Text>
                    </Box>
                ))}
                <Box p={4} borderWidth="1px" borderRadius="md" color={palette.accent}>
                    <Heading as="h3" size="lg" mb={2}>
                        A Aventura da Transição de Carreira 
                    </Heading>
                    <Text>Conteúdo da nova postagem...</Text>
                </Box>
            </VStack>
        </StyledBlog>
    );
};

export default Blog;
